BEGIN TRANSACTION;

DROP TABLE IF EXISTS "esmartdata_instructor";

CREATE TABLE IF NOT EXISTS "esmartdata_instructor" (
  "id" integer NOT NULL,
  "first_name" text NOT NULL,
  "last_name" text NOT NULL,
  "description" text NOT NULL,
  PRIMARY KEY("id" AUTOINCREMENT)
);

DROP TABLE IF EXISTS "esmartdata_course";

CREATE TABLE IF NOT EXISTS "esmartdata_course" (
  "id" integer NOT NULL,
  "title" text NOT NULL,
  "subtitle" text NOT NULL,
  "description" text NOT NULL,
  "category" text NOT NULL,
  "subcategory" text NOT NULL,
  "language" text NOT NULL,
  "length" text NOT NULL,
  "rating" real NOT NULL,
  "referral_link" text NOT NULL,
  "instructor_id" integer NOT NULL,
  PRIMARY KEY("id" AUTOINCREMENT),
  FOREIGN KEY("instructor_id") REFERENCES "esmartdata_instructor"("id")
  ON DELETE CASCADE ON UPDATE CASCADE
);

DROP TABLE IF EXISTS "esmartdata_learningpath";

CREATE TABLE IF NOT EXISTS "esmartdata_learningpath" (
  "id" integer NOT NULL,
  "title" text NOT NULL,
  "subtitle" text NOT NULL,
  "url" text NOT NULL,
  PRIMARY KEY("id" AUTOINCREMENT)
);

DROP TABLE IF EXISTS "esmartdata_learningpath_courses";

CREATE TABLE IF NOT EXISTS "esmartdata_learningpath_courses" (
  "id" integer NOT NULL,
  "learningpath_id" integer NOT NULL,
  "course_id" integer NOT NULL,
  PRIMARY KEY("id" AUTOINCREMENT),
  FOREIGN KEY("course_id") REFERENCES "esmartdata_course"("id")
  ON DELETE CASCADE ON UPDATE CASCADE,
  FOREIGN KEY("learningpath_id") REFERENCES "esmartdata_learningpath"("id")
  ON DELETE CASCADE ON UPDATE CASCADE
);


DROP INDEX IF EXISTS "esmartdata_course_instructor_id_idx";
CREATE INDEX IF NOT EXISTS "esmartdata_course_instructor_id_idx" ON "esmartdata_course" ("instructor_id");

DROP INDEX IF EXISTS "esmartdata_learningpath_courses_learningpath_id_idx";
CREATE INDEX IF NOT EXISTS "esmartdata_learningpath_courses_learningpath_id_idx" ON "esmartdata_learningpath_courses" ("learningpath_id");

DROP INDEX IF EXISTS "esmartdata_learningpath_courses_course_id_idx";
CREATE INDEX IF NOT EXISTS "esmartdata_learningpath_courses_course_id_idx" ON "esmartdata_learningpath_courses" ("course_id");

DROP INDEX IF EXISTS "esmartdata_learningpath_courses_learningpath_id_course_id_idx";
CREATE UNIQUE INDEX IF NOT EXISTS "esmartdata_learningpath_courses_learningpath_id_course_id_idx" ON "esmartdata_learningpath_courses" ("learningpath_id", "course_id");

COMMIT;